.hdr-toggle {
  margin-left: 18px;
  margin-right: 24px;
}
.hdr-toggle input {
  position: absolute;
  margin-left: -9999px;
  visibility: hidden;
}
.hdr-toggle input + label {
  width: 60px;
  height: 24px;
  display: block;
  position: relative;
  cursor: pointer;
  outline: none;
  -webkit-user-select: none;
     -moz-user-select: none;
      -ms-user-select: none;
          user-select: none;
  margin: 0;
}
.hdr-toggle input + label:before,
.hdr-toggle input + label:after {
  display: block;
  position: absolute;
  top: 1px;
  left: 1px;
  bottom: 1px;
  content: "";
}
.hdr-toggle input + label:before {
  right: 1px;
  background-color: rgba(255, 255, 255, 0.45);
  border-radius: 60px;
  transition: background 0.4s;
}
.hdr-toggle input + label:after {
  width: 24px;
  background-color: #fff;
  border-radius: 100%;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
  transition: margin 0.4s;
}
.hdr-toggle input:checked + label:after {
  margin-left: 40px;
}
